import axios from 'axios';
import React, { useState } from 'react';
import { useHistory } from 'react-router';
import { Button, Form } from 'semantic-ui-react';

export default function CreateRestaurante() {
    const history = useHistory();

    const [nombre, setNombre] = useState('');
    const [tipo, setTipo] = useState('');
    const [direccion, setDireccion] = useState('');
    const [telefono, setTelefono] = useState('');
    const [horario, setHorario] = useState('');
    const [rating, setRating] = useState('');

    const postDataRes = (e) => {
        e.preventDefault(); // Prevenir el comportamiento predeterminado del formulario
        axios.post('http://localhost:2031/restaurantes/postRestauran', {
            nombre,
            tipo,
            direccion,
            telefono,
            horario,
            rating,
        }).then(() => {
            history.push('/');
        });
    };

    return (
        <div>
            <Form className='create-form' onSubmit={postDataRes}> {/* Agregar onSubmit para manejar el envío del formulario */}
                <Form.Field>
                    <label>Nombre</label>
                    <input placeholder='Ingrese el nombre' type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} />
                </Form.Field>

                {/* Resto de los campos de entrada aquí... */}

                <Button type='submit'>Crear</Button> {/* Tipo 'submit' para el botón de envío */}
            </Form>
        </div>
    );
}
