import React, { useEffect, useState } from "react";
import { Button, Form } from 'semantic-ui-react';
import axios from "axios";
import { useHistory } from "react-router-dom"; // Importa desde 'react-router-dom'

export default function UpdatePicadas() {
    const history = useHistory();

    const [_id, setID] = useState(null);
    const [nombre, setNombre] = useState('');
    const [precio, setPrecio] = useState('');

    useEffect(() => {
        setID(localStorage.getItem("ID"));
        setNombre(localStorage.getItem('Nombre'));
        setPrecio(localStorage.getItem('Precio'));
    }, []);

    const updateAPIData = () => {
        axios.put(`http://localhost:2031/picadas/putPicadas/${_id}`, {
            nombre,
            precio,
        }).then(() => {
            history.push('/readPicadas');
        });
    }

    return (
        <div>
            <Form className='create-form'>
                <Form.Field>
                    <label>Nombre</label>
                    <input placeholder='Nombre' type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} />
                </Form.Field>

                <Form.Field>
                    <label>Precio</label>
                    <input placeholder='Precio' value={precio} onChange={(e) => setPrecio(e.target.value)} />
                </Form.Field>

                <Button type='button' onClick={updateAPIData}>Actualizar</Button>
            </Form>
        </div>
    );
}
