import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Table, Button } from 'semantic-ui-react';
import { Link } from 'react-router-dom';

export default function Read(){
    const [ APIData, setAPIData] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:2031/combos/getCombo')
        .then((response) => {
            console.log(response.data);
            setAPIData(response.data);
        })
    }, []);

    const setData = (data) => {
        let { _id, descripcion, precio, comida, bebida} = data;
        localStorage.setItem('ID', _id);
        localStorage.setItem('Descripcion', descripcion);
        localStorage.setItem('Precio', precio);
        localStorage.setItem('Comida', comida);
        localStorage.setItem('Bebida', bebida);
    };

    const getData = () => {
        axios.get('http://localhost:2031/combos/getCombo')
        .then((getData) => {
            getData(getData.data);
        })
    };

    const oneDelete = (id) => {
        axios.delete(`http://localhost:2031/combos/${id}`)
    };



    return (
        <div>
            <Table singleLine>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Descripcion</Table.HeaderCell>
                        <Table.HeaderCell>Precio</Table.HeaderCell>
                        <Table.HeaderCell>Comida</Table.HeaderCell>
                        <Table.HeaderCell>Bebida</Table.HeaderCell>
                        <Table.HeaderCell>Eliminar</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>
                <Table.Body>
                    {APIData.map((data) => (
                        <Table.Row key={data._id}>
                            <Table.Cell>{data.descripcion}</Table.Cell>
                            <Table.Cell>{data.precio}</Table.Cell>
                            <Table.Cell>{data.comida}</Table.Cell>
                            <Table.Cell>{ob.bebida}</Table.Cell>
                            <Table.Cell>
                                <Link to="/update">
                                    <Button onClick={() => setData(data)}>Actualizar</Button>
                                </Link>
                            </Table.Cell>
                            <Table.Cell>
                                <Button onClick={() => oneDelete(data.id)}>Eliminar</Button>
                            </Table.Cell>
                        </Table.Row>
                    ))}
                </Table.Body>
            </Table>
        </div>
    );
}
