import './../app.css'
import React, { useEffect, useState } from 'react';
import { Card, Button } from 'semantic-ui-react';
import axios from 'axios';

export default function Read() {
  const [APIData, setAPIData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:2031/combos/getCombo').then((response) => {
      console.log(response.data);
      setAPIData(response.data);
    });
  }, []);

  const setData = (data) => {
    localStorage.setItem('ID', data._id);
    localStorage.setItem('Descripcion', data.descripcion);
    localStorage.setItem('Precio', data.precio);
    localStorage.setItem('Comida', data.comida);
    localStorage.setItem('Bebida', data.bebida);
  };

  const oneDelete = (_id) => {
    axios.delete(`http://localhost:2031/combos/deleteCombo/${_id}`);
  };

  return (
    <div  className='complete'>
  {APIData.map((data) => (
    <Card key={data._id}>
      <Card.Content>
        <Card.Header>{data.descripcion}</Card.Header>
        <Card.Meta>Precio: {data.precio}</Card.Meta>
        <Card.Description>
          <strong>Comida:</strong> {data.comida} <br />
          <strong>Bebida:</strong> {data.bebida}
        </Card.Description>
      </Card.Content>
      <Card.Content extra>
        <div className='ui two buttons'>
          
          <Button basic color='green' onClick={() => setData(data)}>Actualizar
          </Button>
          <Button basic color='red' onClick={() => oneDelete(data._id)}>Eliminar
          </Button>
        </div>
      </Card.Content>
    </Card>
  ))}
</div>

  );
}
