import React, { useState } from "react";
import { Button, Form } from 'semantic-ui-react';
import axios from "axios";
import { useNavigate } from "react-router-dom";

function createBurguer () {
    const navigate = useNavigate(); // Usamos useNavigate en lugar de useHistory

    const [nombre, setNombre] = useState('');
    const [precio, setPrecio] = useState('');


    const postData = () => {
        axios.put(`http://localhost:2031/burguers/postHam`, {
            nombre,
            precio,
        }).then(() => {
            navigate('/readBurguers'); // Usamos navigate para redirigir
        });
    }

    return (
        <div>
            <Form className='create-form' onSubmit={postData}>
                <Form.Field>
                    <label>Nombre</label>
                    <input placeholder='Nombre' type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} />
                </Form.Field>

                <Form.Field>
                    <label>Precio</label>
                    <input placeholder='Precio' value={precio} onChange={(e) => setPrecio(e.target.value)} />
                </Form.Field>

                <Button type='submit'>Actualizar</Button>
            </Form>
        </div>
    );
}

export default createBurguers
